export default {
  updateAwardExt(state, payLoad) {
    state.AWARD_EXT = payLoad;
  },
  updateGoldCount(state, payLoad) {
    state.gold_count = payLoad;
  },
  addGoldCount(state, payLoad) {
    state.gold_count += payLoad;
  },
  updateIsPad(state, payLoad) {
    state.IS_IPAD = payLoad;
  },
  updateSystem(state, payLoad) {
    state.SYSTEM_INFO = payLoad;
  },
  updateHideApp(state, payLoad) {
    state.IS_HIDE_MINI_APP = payLoad;
  },
  updateCoreStudied(state, payLoad) {
    state.coreStudied = payLoad;
  },
  updateWordswitch(state, payLoad) {
    state.wordswitch = payLoad;
  },
};
