export default {
  updateStep(state, status) {
    state.step = status;
  },

  updateRecordingStatus(state, status) {
    state.recordingStatus = status;
  },

  updateAudioPlayStatus(state, status) {
    state.audioPlayStatus = status;
  },

  updateRecordRes(state, payload) {
    state.recordRes = payload;
  },
};
