<!-- 录音权限授权引导蒙层 -->
<template>
  <view class="auth_mask">
    <view class="auth_box">
      <view>小朋友，进行音频授权后，就可以参与录音相关的互动哦~</view>
      <button class="auth_btn" @click="gotoAuth">去授权</button>
    </view>
  </view>
</template>

<script>
export default {
  methods:  {
    // 去授权
    gotoAuth() {
      const _this = this;
      // 点去授权;
      wx.openSetting({
        success: res => {
          const {
            authSetting = {}
          } = res;
          if (authSetting["scope.record"]) {
            _this.$emit('callback', true)
          } else {
            _this.$emit('callback', false)
          }
            
        },
        fail(e) {
          _this.$emit('callback', false)
        }
      });
    }
  }
}
</script>

