import { StatsDataPoolManager } from './data-pool';
import { StatsComposer } from './composer';
import { StatsPlatform } from './platform';
import { StatsLogger } from './logger';
import { StatsConfigService, StatsConfig } from './config';
/**
 * sdk core 主类option
 */
export interface StatsMainOption {
    platform?: StatsPlatform;
}
/**
 * sdk core 主类
 */
export default class StatsMain {
    /**
     * 数据服务
     */
    private dataPoolManager;
    /**
     * 逻辑处理器
     */
    private composer;
    /**
     * 平台实例
     */
    private platform?;
    /**
     * 日志服务
     */
    private logger;
    /**
     * 配置服务
     */
    private config;
    constructor(options: StatsMainOption, config: StatsConfig);
    /**
     * 获取处理器
     */
    get $composer(): StatsComposer;
    /**
     * 获取数据管理器
     */
    get $dm(): StatsDataPoolManager;
    /**
     * 获取平台实例
     */
    get $platform(): StatsPlatform | undefined;
    /**
     * 获取日志服务
     */
    get $logger(): StatsLogger;
    /**
     * 获取全局配置
     */
    get $config(): StatsConfigService;
    /**
     * 初始化sdk core
     * @param cb success callback
     */
    init(cb?: () => void): Promise<void>;
    /**
     * 手动打点接口
     * @param name 点位名
     * @param params 点位业务参数
     */
    track(name: string, params: object): void;
}
