import { getPromiseFunc } from "../utils/tool";

interface copyToClipboardParams {
    content: String;  // 要复制到剪贴板的内容
    hint: String; // 复制成功的提示文案
    businessData?: any;
}

const defaultParams: copyToClipboardParams = {
    content: "",
    hint: ""
};

/**
 * 复制文案到剪贴板
 * @param params 
 */
export function copyText(params: copyToClipboardParams): any {
    let _params: copyToClipboardParams = {
        ...defaultParams,
        ...params
    };

    return getPromiseFunc("core_copyToClipboard", _params);
}