import {hybrid as postMessageHybrid} from "../utils/hybrid"
import {hybrid as iframeHybrid} from "../utils/iframeHybrid"

// const newApi: boolean = +switchConfig.jsBridge_isNewJsBridge === 1;
// export let hybrid = newApi ? postMessageHybrid : iframeHybrid;
export function hybrid(action: string, params?: any, callback?: Function) {
    if(action.indexOf('core_') === 0){
        postMessageHybrid(action, params, callback);
    }else{
        iframeHybrid(action, params, callback);
    }
}