import { getPromiseFunc, resolveCallbackFactory, rejectCallbackFactory} from "../utils/tool";

interface isAppInstalledParams {
    url: String;  // 平台协议
}

/**
 * 监测应用是否安装
 * @param params 
 */
export function isAppInstalled(params: any, callback?: Function): any {
    let _params: isAppInstalledParams = {
        url: params.url
    }
    let success= resolveCallbackFactory(callback);
    let fail= rejectCallbackFactory(callback);
    return getPromiseFunc("core_isAppInstalled", _params).then(success,fail);
    
}