import { getPromiseFunc } from "../utils/tool";
import { isInApp, switchConfig } from "../utils/system";

/**
 * 关闭进入页面后系统的 loading
 */
export function removeNativeLoading(){
    return getPromiseFunc("core_removeLoading");
}

// 进入页面3秒后如果系统还没有关闭loading，hybrid-sdk做陡地来关闭loading
isInApp && switchConfig.isNewJsBridge && setTimeout(()=>{
    removeNativeLoading();
},3000);
