import { getPromiseFunc } from "../utils/tool";

interface showWebPictureParams {
    imgUrl: String[];           // 图片地址列表
    index?: number;              // 默认展示图片的索引
    showDownloadBtn?: number;    // 是否显示 下载图片按钮
    businessData?: any;
}

const _defaultParams: showWebPictureParams = {
    imgUrl: [], 
    index: 0,
    showDownloadBtn: 0
}


/**
 * 图片浏览器
 * @param params 
 */
export function imgBrowser (params: any): any {
    let _params = {
        ...params
    }

    return getPromiseFunc("core_showImgBrowser", _params);

}