import { hybrid } from "../utils/hybrid";

interface videoDisplayParams {
    type:String;       // hint(playVideoHint), live(playLiveVideo), pure(playPureVideo) ,rtc(platformRTCVideo) 默认:playVideo
    playUrl:String;    // video地址
    businessData?: any;
    [propName: string]: any;
}

/**
 * 展示提示框
 * @param params 
 */
export function playVideo(params: any): void {
    let playUrl = '';
    switch(params.type){
        case 'hint':
            playUrl = params.playUrl;
            break;
        case 'pure':
            playUrl = params.sourceUrl;
            break;
        case 'live':
            playUrl = params.url;
            break;
        case 'rtc':
            playUrl = params.playUrl;
            break; 
        
        default: playUrl = params.playUrl;
    }
    let _params: videoDisplayParams = {
        type: params.type,
        playUrl: playUrl 
    };
    delete params.type;
    _params.businessData = params;
    
    hybrid('core_videoDisplay', _params);
}