import { hybrid } from '../../../utils/iframeHybrid'

/**
 * 实时日志上传
 * @export
 * @param {object} data
 *  @property {string} pid   业务名称,必须传,后端约定,注意 pid 是字符串格式 不是数字,
 *  @property {string} event 事件名称,必须传
 *  @property {object} data  参数json串 可以不传
 */
export function runtimeLog (data: any) {
  hybrid('logReport', data)
}

/**
 *普通日志,控制台显示
 * @export
 * @param {object} data
 *  @property {string} content 日志内容
 */
export function appConsoleLog (data: any) {
  if (!data || !data.content) {
    console.error('content is undefined')
  }
  hybrid('logcat', data)
}
