/**
 *添加到日历控件中
 * @export
 * @param {object}   options
 *  @property {Number}    courseId    (与课程id没有本质的联系,只是端上区分提醒的id)
 *  @property {Number}    option_add  1是添加,0是取消
 *  @property {Array}     event        时间数组
 *   @property {Number}   remindTime
 *   @property {Number}   remindTimeEnd
 *   @property {String}   remindTitleNa
 * @param {Function} callback
 * @returns {object}
 *  @property {boolean} isMark  添加本地购买提醒
 */
export function showCalendar (options = {}, callback: Function) {
  if (!options) {
    console.error('calendarWeb data is required')
    return false
  }

  hybrid('AddToCalendarWebAction', options, callback)
}
