import { hybrid } from '../../../utils/iframeHybrid'
import Type from '../../../utils/type'

/**
 * http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_camera.html
 */

/**
 * 为h5运营提供的拍照或从图库取一个图片上传，回调函数返回上传图片的地址
 * @param {Object} options
 *  @property {Number} type 必填
 *  @property {String} tipMsg
 *  @property {Number} photoId
 */
export function photoUpload (options: any, callback: Function) {
  if (!Type.isFunction(callback)) {
    console.error('callback is not a function')
    return
  }
  hybrid('cameraUpload', options, callback)
}

/**
 *拍照搜题
 */
export function photoQuestion () {
  hybrid('openCamera')
}
