import { hybrid } from '../../../utils/iframeHybrid'

/**
 * http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_other.html
 */

/**
 * 与机器人聊天
 * @param {Object} options
 *  @property {string} url 当前H5页面的地址
 *  @property {string} fr  来源
 */
export function chatWithRobot (options: any) {
  if (!options || !options.url) {
    console.error('chat url is undefined')
    return false
  }
  if (!options.fr) {
    console.error('chat fr is undefined')
    return false
  }
  hybrid('openRobotChat', options)
}
