import { hybrid } from '../../../utils/iframeHybrid'
import Type from '../../../utils/type'
/**
 * http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_other.html
 */

/**
 * @description   老师说 - 评论回复
 * @param {Striing} atNickname
 * @param {Function} callback
 */
export function commentReply (data: any, callback: Function) {
  var { atNickname } = data
  if (!atNickname) {
    console.error('atNickname is undefined')
    return
  }

  if (!Type.isFunction(callback)) {
    console.error('callback is not function')
    return
  }

  hybrid('commentReply', data, callback)
}

/**
 * @description 老师说 - 详情页
 * @param {String} url
 * @param {Function} callback
 */
export function teacherCommentDetail (data: any) {
  var { url, msgId } = data
  if (!url || !msgId) {
    console.error('url or msgId is undefined')
    return
  }
  hybrid('teacherMessageDetail', data)
}
