import { hybrid } from '../../../utils/iframeHybrid'

// 窗口：http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_window.html
// 滚动：http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_other.html

/**
 * @description fe通过Action自动关闭na的h5页面
 * @param {Object} options
 *
 */
export function closeWebPage (data: any) {
  hybrid('liveExitWebPage', data)
}

/**
 * @description fe打开一个h5页面
 * @param {Object} options
 *
 */
export function openWebPage (data: any) {
  hybrid('liveOpenWebPage', data)
}

/**
 * @description fe控制h5插件整个区域是不可点击
 * @param {Object} options
 *
 */
export function disableClickWholeH5Page () {
  hybrid('canClickableWholeH5Page', { clickable: 1 })
}

/**
 * @description fe控制h5插件整个区域是可点击
 * @param {Object} options
 *
 */
export function ableClickWholeH5Page () {
  hybrid('canClickableWholeH5Page', { clickable: 0 })
}
