import { hybrid } from '../../../utils/iframeHybrid'

/**
 * @description 打开人工客服
 * @param {*} data
 */
export function openCustomerService (data: any) {
  hybrid('openOnlineService', data)
}

/**
 * @description 打开缓存列表
 * @param {*} data
 */
export function openCachelist () {
  hybrid('openCachelist')
}

/**
 * @description 进入订单
 * @param {*} data
 */
export function openSalelist () {
  hybrid('openSalelist')
}

/**
 * @description 打开一个新的webveiw，只适配订单的webview
 * @param {*} data
 */
export function openOrderWindow (data: any) {
  var { url, evalStr } = data
  if (!url) {
    console.error('openOrderWindow url is not function')
    return
  }

  if (!evalStr) {
    evalStr = 'javascript:window&&window.orderListPageRefresh&&window.orderListPageRefresh();void(0);'
  }
  var paremters = {
    'url': url,
    'evalStr': evalStr
  }
  hybrid('openOrderWindow', paremters)
}
