import { hybrid } from '../../../utils/iframeHybrid'

// http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_titlebar.html

const ACTION = 'commonHideTitleBar'
/**
 * @description 隐藏标题栏
 * @method hideTitleBar
 */
export function hideTitleBar () {
  hybrid(ACTION, {
    is_hide: 1
  })
}

/**
 * @description 显示标题栏
 * @method showTitleBar
 */
export function showTitleBar () {
  hybrid(ACTION, {
    is_hide: 0
  })
}

/**
 * @description 修改标题栏文案
 * @method TitleBarText
 * @param options{String} title  //标题内容
 */
export function editBarTitle (data: any) {
  if (!data || !data.title) {
    console.error('title is undefined')
    return
  }
  hybrid('commonUpdateBarTitle', data)
}
