import { hybrid } from '../../../utils/iframeHybrid'

// http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_vedio.html

/**
 * @description 播放视频,竖屏播放的,录播售卖,无业务播放器
 * @param {Object} options
 *   @property {String} type //hint:竖屏播放的action,live:录播售卖,pure:无业务播放器.默认：播放视频
 */
export function playVideo (data: any) {
  if (!data) {
    console.error('param is undefined!')
    return false;
  }
  var action = ''
  switch (data.type) {
    case 'hint':
      action = 'playVideoHint' // 竖屏播放的action
      break
    case 'live':
      action = 'playLiveVideo' // 播放直播视频
      break
    case 'pure':
      action = 'playPureVideo' // 无业务播放器
      break
    default:
      action = 'playVideo' // 播放视频
      break
  }

  hybrid(action, data)
}
