import { hybrid } from '../../../utils/iframeHybrid'

// http://qatest7.suanshubang.com/static/fe-docs/_book/hybrid-sdk/app_other.html

/**
 * @description fe控制h5插件整个区域是不可点击
 * @param {Object} options
 *
 */
export function disableWebScroll () {
  hybrid('disableWebScroll', { isDisable: 1 })
}

/**
 * @description fe控制h5插件整个区域是可点击
 * @param {Object} options
 *
 */
export function ableWebScroll () {
  hybrid('disableWebScroll', { isDisable: 0 })
}
