import { hybrid as handleHybrid, createHybridCallback } from '../../../utils/iframeHybrid'

/**
 * @description: 智能关注关闭打点
 * @param {type}
 * @return:
 */
interface exerciseFinishParams {
 data?: any;
 success?: Function;
 notFound?: Function;
};
export function exerciseFinish (options: exerciseFinishParams = {}) {
  if (!handleHybrid) {
    console.error('hybrid is undefined')
    return
  };

  var { data, success, notFound } = options;
  var callback = createHybridCallback(success, notFound);
  (<HybridFunc>handleHybrid)('webExerciseFinish', data, callback);
}
