import { hybrid as handleHybrid } from '../../../utils/iframeHybrid'

/**
 *
 * @param {Object} options
 *  @property url 下载地址
 *  @property type
 */
export function download (options: any, callback: Function) {
  if (!options) {
    console.error('data is undefined')
    return false
  }
  if (!options.url) {
    console.error('url is undefined')
    return false
  }

  if (!options.type) {
    console.error('type is undefined')
    return false
  }
  handleHybrid('downloadMedia', options, callback)
}
