import { checkIsInApp } from '../../../utils/system'
import { winEnterLiveRoom, winEnterVedioReplay } from '../winActions/windows'

/**
 * 进入直播间
 * @param {Function} options
 */
export function enterLiveRoom (options: any) {
  var { url } = options
  if (!url) {
    console.error('url is not defined')
    return
  }
  if (checkIsInApp()) {
    window.location.href = url
  } else {
    winEnterLiveRoom({
      url: url
    })
  }
}

/**
 * 进入回放教室
 * @param {Function} options
 */
export function enterPlayBackRoom (options: any) {
  var { url } = options
  if (!url) {
    console.error('url is not defined')
    return
  }

  if (checkIsInApp()) {
    window.location.href = url
  } else {
    winEnterVedioReplay({
      url: url
    })
  }
}
