import { hybrid} from '../../../utils/iframeHybrid'

/**
 * @description 网络数据请求  httpRequst
 * @param {Object} options
 *  @property {String} type //type=post
 */
interface requestParams {
  type?: string;
  url?: string;
  params?: any;
};
export function request (data: requestParams, callback: Function) {

  if (!data.type) {
    console.error('http type is required')
  } else if (data.type !== 'post') {
    console.error('http type only support post')
  } else if (!data.url) {
    console.error('http url is required')
  } else if (!data.params) {
    data.params = {}
  } else {
    return hybrid('httpRequst', data, callback);
  }
}
