import { hybrid as handleHybrid } from '../../../utils/iframeHybrid'

/**
 * @description 听后题回答后加分 
 * @param {Object} data
 * @param {Number} data.isRight  对 错
 * @param {Number} data.content  附带的内容
 * @param {Number} data.rightKey 正确答案
 * @param {Number} data.score    得分
 * @param {Function} callback
 */

interface liveCourseAnswerResult {
  isRight: number;
  content: string;
  rightKey: string;
  score: number;
}
export function liveCourseAnswerResult (data: liveCourseAnswerResult, callback: Function) {
  return handleHybrid('liveCourseAnswerResult', data, callback)
}

export function submitTestSuccess (callback: Function) {
  return handleHybrid('submitTestSuccess', callback)
}
