import { hybrid as handleHybrid } from '../../../utils/iframeHybrid'

/**
 * @description  时间校准
 * @param {Function} startTime          
 * @param {Function} callback
 */
interface timeSyncParams {
  startTime: string;
};
export function timeSync (data: timeSyncParams, callback: Function) {
  if (Object.prototype.toString.call(callback) !== '[object Function]') {
    console.error('callback is not function')
    return false
  }
  (<HybridFunc>handleHybrid)('TimeSynchronization', data, callback)
}
