import { hybrid as handleHybrid } from '../../../utils/iframeHybrid'

/**
 *@description: toast弹窗提醒
 *@method Toast
 * @param options{Object}
 *    @property action{String}  //action名称 text
 *    @property data{Object}    //data数据对象
 */
export function toast (data: any) {
  if (!data.text) {
    console.error('toast text is undefined')
    return false
  }
  handleHybrid('toast', data)
}
