/**
 * @description 类型检查
 * @param {Object} parameters 参数
 * @example
 * Type.isArray([]);//true
 * Type.isFunction(function(){});//true
 * Type.isString(function(){});//true
 *  */

var Type = {}

var typeArray = ['String', 'Number', 'Array', 'Function']

for (var i = 0, len = typeArray.length; i < len; i++) {
  var type = typeArray[i];
  (function (type) {
    Type['is' + type] = function (parameters) {
      return Object.prototype.toString.call(parameters) === '[object ' + type + ']'
    }
  })(type)
}

export default Type
