import {core_audio} from  'hybrid-sdk-common'

/**
 * 播放音频
 * @param params 
 */
export function playAudio(params: any): void {
    let _params = {
        ...params
    };
    _params.data.playStatus = 1;
    core_audio(_params);
}


/**
 * 暂停音频
 * @param params 
 */
export function stopAudio(params: any): void {

    let _params = {
        ...params
    };
    _params.data.playStatus = -1;

    core_audio(_params);
}


/**
 * 暂停音频
 * @param params 
 */
export function pauseAudioZyb(): void {
    
    let _params = {
        data: {
            playStatus: 0,
            url: ""
        }
    };

    core_audio(_params);
}

/**
 * 暂停音频
 * @param params 
 */
export function stopAudioZyb(): void {
    
    let _params = {
        data: {
            playStatus: -1,
            url: ""
        }
    };

    core_audio(_params);
}