import {fetchImg} from  'hybrid-sdk-common'
import {resolveCallbackFactory, rejectCallbackFactory } from "../utils/tool";

/**
 * 图片浏览器
 * @param params 
 */
export function photoUpload(params: any, callback?: Function): void {

    let success= resolveCallbackFactory(callback);
    let fail= rejectCallbackFactory(callback);
    
    return fetchImg(params).then(success, fail);
}