import {core_request} from  'hybrid-sdk-common'
import {resolveCallbackFactory, rejectCallbackFactory } from "../utils/tool";

/**
 * 通过native调用接口, 适配了之前的API
 * @param params 
 */
export function request(params: any, callback?: Function): void {

    let success= resolveCallbackFactory(callback);
    let fail= rejectCallbackFactory(callback);

    return core_request(params).then(success, fail);
}


