import {nativeLog} from  'hybrid-sdk-common'

/**
 * 数据埋点
 * @param params
 */
export function commonLog(params: any): void {
    const _params = {
        name: params.name,
        param: params.data,
        act: params.act || 0
    };
    nativeLog(_params);
}

/**
 * 数据埋点, 服务端3分钟上传
 * @param params
 */
export function runtimeLog(params: any): void {
    nativeLog(params);
}

/**
 * 数据埋点, native控制台日志
 * @param params
 */
export function appConsoleLog(params: any): void {
    nativeLog(params);
}