import {netType} from  'hybrid-sdk-common'

/**
 * 获取网络状态, 适配之前的API
 * @param params 
 */
export function isWifi(callback?: Function): void {
    let success = function(res: any = {}){
        
        callback && callback(res);
        return Promise.resolve(res);
    };
    let fail = function(res: any){
        callback && callback(res);
        return Promise.reject(res);
    };

    return netType().then(success, fail);
}