import {core_showDialog} from 'hybrid-sdk-common'
import {resolveCallbackFactory, rejectCallbackFactory } from "../utils/tool";

/**
 * 展示提示框
 * @param params 
 */
export function showDialog(params: any, callback?: Function): void {
    let success= resolveCallbackFactory(callback);
    let fail= rejectCallbackFactory(callback);
    return core_showDialog( params).then(success, fail);
}